import React, { useEffect, useState } from 'react';

const Dashboard = () => {
  const [stats, setStats] = useState({
    properties: 0,
    units: 0,
    tenants: 0,
    agents: 0,
  });

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const [propertiesRes, unitsRes, tenantsRes, agentsRes] = await Promise.all([
          fetch("http://localhost:5000/properties"),
          fetch("http://localhost:5000/units"),
          fetch("http://localhost:5000/tenants"),
          fetch("http://localhost:5000/agents"),
        ]);

        const [properties, units, tenants, agents] = await Promise.all([
          propertiesRes.json(),
          unitsRes.json(),
          tenantsRes.json(),
          agentsRes.json(),
        ]);

        setStats({
          properties: properties.length,
          units: units.length,
          tenants: tenants.length,
          agents: agents.length,
        });
      } catch (error) {
        console.error("Error fetching stats:", error);
      }
    };

    fetchStats();
  }, []);

  return (
    <div style={{ padding: "2rem" }}>
      <h2 style={{ marginBottom: "1.5rem", fontSize: "1.8rem", fontWeight: "bold" }}>Dashboard</h2>
      <div style={{ display: "grid", gridTemplateColumns: "repeat(4, 1fr)", gap: "1rem" }}>
        <div style={cardStyle}>
          <h3>Properties</h3>
          <p style={numberStyle}>{stats.properties}</p>
        </div>
        <div style={cardStyle}>
          <h3>Units</h3>
          <p style={numberStyle}>{stats.units}</p>
        </div>
        <div style={cardStyle}>
          <h3>Tenants</h3>
          <p style={numberStyle}>{stats.tenants}</p>
        </div>
        <div style={cardStyle}>
          <h3>Agents</h3>
          <p style={numberStyle}>{stats.agents}</p>
        </div>
      </div>
    </div>
  );
};

const cardStyle = {
  backgroundColor: "#fff",
  borderRadius: "8px",
  padding: "1.5rem",
  boxShadow: "0 0 8px rgba(0,0,0,0.1)",
  textAlign: "center",
};

const numberStyle = {
  fontSize: "2rem",
  fontWeight: "bold",
  marginTop: "0.5rem",
};

export default Dashboard;
