import React, { useState, useEffect, useMemo } from 'react';
import { getPayments, getLeases, getTenants, addPayment, updatePayment, deletePayment } from '../api.js';
import AddFormModal from './AddFormModal.jsx';
import '../app.css';

const Payments = ({ searchQuery }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [payments, setPayments] = useState([]);
  const [leases, setLeases] = useState([]);
  const [tenants, setTenants] = useState([]);
  const [editingPayment, setEditingPayment] = useState(null);
  const [loading, setLoading] = useState(true);

  // Refresh data from backend
  const refreshData = async () => {
    setLoading(true);
    try {
      const [paymentsData, leasesData, tenantsData] = await Promise.all([
        getPayments(),
        getLeases(),
        getTenants()
      ]);
      setPayments(paymentsData);
      setLeases(leasesData);
      setTenants(tenantsData);
    } catch (error) {
      console.error("Failed to fetch data:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    refreshData();
  }, []);

  // Build lease options dynamically
  const paymentFields = useMemo(() => [
    {
      name: 'lease_id',
      label: 'Lease',
      type: 'select',
      options: leases.map(lease => {
        const tenant = tenants.find(t => t.id === lease.tenant_id);
        return {
          value: lease.id,
          label: `Lease ID ${lease.id} - Tenant: ${tenant ? tenant.name : 'Unknown'}`
        };
      })
    },
    { name: 'amount', label: 'Amount', type: 'number', placeholder: 'Enter payment amount' },
    { name: 'date', label: 'Date', type: 'date' },
    {
      name: 'method',
      label: 'Method',
      type: 'select',
      options: [
        { value: 'cash', label: 'Cash' },
        { value: 'bank transfer', label: 'Bank Transfer' },
        { value: 'card', label: 'Card' },
        { value: 'mobile money', label: 'Mobile Money' }
      ]
    },
    {
      name: 'status',
      label: 'Status',
      type: 'select',
      options: [
        { value: 'paid', label: 'Paid' },
        { value: 'pending', label: 'Pending' },
        { value: 'overdue', label: 'Overdue' }
      ]
    }
  ], [leases, tenants]);

  const handleOpenModal = (payment = null) => {
    setEditingPayment(payment);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setEditingPayment(null);
  };

  const handleAddOrUpdatePayment = async (newPayment) => {
    try {
      if (editingPayment) {
        await updatePayment(editingPayment.id, newPayment);
      } else {
        await addPayment(newPayment);
      }
      refreshData();
    } catch (error) {
      console.error("Failed to save payment:", error.message);
    } finally {
      handleCloseModal();
    }
  };

  const handleDeletePayment = async (id) => {
    try {
      await deletePayment(id);
      refreshData();
    } catch (error) {
      console.error("Failed to delete payment:", error.message);
    }
  };

  const getLeaseDetails = (leaseId) => {
    const lease = leases.find(l => l.id === leaseId);
    if (!lease) return { tenantName: 'Unknown', propertyName: 'Unknown' };
    const tenant = tenants.find(t => t.id === lease.tenant_id);
    return {
      tenantName: tenant ? tenant.name : 'Unknown',
      propertyName: lease.unit ? lease.unit.property.name : 'Unknown'
    };
  };

  const filteredPayments = payments.filter(payment => {
    const { tenantName } = getLeaseDetails(payment.lease_id);
    const searchTerm = searchQuery.toLowerCase();
    return tenantName.toLowerCase().includes(searchTerm) ||
      String(payment.amount).includes(searchTerm) ||
      payment.date.includes(searchTerm) ||
      payment.method.toLowerCase().includes(searchTerm);
  });

  if (loading) {
    return (
      <div className="page-content">
        <h1>Loading Payments...</h1>
      </div>
    );
  }

  return (
    <div className="page-content">
      <div className="page-header">
        <div>
          <h1 style={{ margin: 0, fontSize: '1.5rem', fontWeight: 'bold' }}>Payments</h1>
          <p style={{ margin: '0.5rem 0 0', color: '#6c757d' }}>Manage your incoming rental payments</p>
        </div>
        <button onClick={() => handleOpenModal()} className="submit-button">
          + Add Payment
        </button>
      </div>

      <div className="table-container">
        <table>
          <thead>
            <tr>
              <th>Tenant</th>
              <th>Amount</th>
              <th>Date</th>
              <th>Method</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredPayments.map(payment => {
              const { tenantName } = getLeaseDetails(payment.lease_id);
              return (
                <tr key={payment.id}>
                  <td>{tenantName}</td>
                  <td>KSH{payment.amount}</td>
                  <td>{payment.date}</td>
                  <td>{payment.method}</td>
                  <td>{payment.status}</td>
                  <td className="action-buttons">
                    <button onClick={() => handleOpenModal(payment)}>Edit</button>
                    <button onClick={() => handleDeletePayment(payment.id)}>Delete</button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>

      {isModalOpen && (
        <AddFormModal
          title={editingPayment ? "Edit Payment" : "Add New Payment"}
          fields={paymentFields}
          initialData={editingPayment} // <-- ensures Status pre-fills
          onSubmit={handleAddOrUpdatePayment}
          onClose={handleCloseModal}
        />
      )}
    </div>
  );
};

export default Payments;
