import { useEffect, useState } from "react";

function Dashboard() {
  const [summary, setSummary] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch("http://localhost:5000/dashboard_summary")
      .then((res) => res.json())
      .then((data) => {
        setSummary(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Error fetching dashboard summary:", err);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <p>Loading dashboard...</p>;
  }

  if (!summary) {
    return <p>No data available</p>;
  }

  return (
    <div style={{ padding: "20px" }}>
      <h2>Dashboard Summary</h2>
      <ul>
        <li>Total Properties: {summary.total_properties}</li>
        <li>Total Tenants: {summary.total_tenants}</li>
        <li>Total Leases: {summary.total_leases}</li>
        <li>Available Units: {summary.available_units}</li>
        <li>Total Rent Due: {summary.total_rent_due}</li>
      </ul>
    </div>
  );
}

export default Dashboard;
