import React, { useState, useEffect } from "react";
import { getDashboardSummary } from "../api.js";
import {
  Home,
  Users,
  FileText,
  DoorOpen,
  Wallet,
} from "lucide-react"; // icons
import "../app.css";

const Dashboard = ({ currentTheme }) => {
  const [summaryData, setSummaryData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const data = await getDashboardSummary();
        setSummaryData(data);
      } catch (error) {
        console.error("Error fetching dashboard data:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchDashboardData();
  }, []);

  if (loading) {
    return (
      <div style={{ padding: "2rem", color: currentTheme?.text || "#000" }}>
        <h1>Loading Dashboard Data...</h1>
      </div>
    );
  }

  const cards = [
    {
      id: 1,
      title: "Total Properties",
      value: summaryData.total_properties,
      icon: <Home size={32} />,
      color: "#4f46e5",
    },
    {
      id: 2,
      title: "Total Tenants",
      value: summaryData.total_tenants,
      icon: <Users size={32} />,
      color: "#16a34a",
    },
    {
      id: 3,
      title: "Total Leases",
      value: summaryData.total_leases,
      icon: <FileText size={32} />,
      color: "#f59e0b",
    },
    {
      id: 4,
      title: "Available Units",
      value: summaryData.available_units,
      icon: <DoorOpen size={32} />,
      color: "#0ea5e9",
    },
    {
      id: 5,
      title: "Total Rent Due",
      value: `Ksh ${summaryData.total_rent_due}`,
      icon: <Wallet size={32} />,
      color: "#dc2626",
    },
  ];

  return (
    <div
      style={{
        padding: "2rem",
        backgroundColor: currentTheme?.background || "#f9fafb",
        minHeight: "100vh",
        color: currentTheme?.text || "#000",
      }}
    >
      <h1 style={{ marginBottom: "2rem", fontSize: "1.8rem", fontWeight: "bold" }}>
        Dashboard
      </h1>

      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(200px, 1fr))",
          gap: "1.5rem",
        }}
      >
        {cards.map((card) => (
          <div
            key={card.id}
            onClick={() => alert(`${card.title} clicked!`)} // ðŸ‘ˆ make it clickable
            style={{
              backgroundColor: "#fff",
              padding: "1.5rem",
              borderRadius: "0.75rem",
              boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
              display: "flex",
              alignItems: "center",
              justifyContent: "space-between",
              cursor: "pointer",
              transition: "transform 0.2s, box-shadow 0.2s",
            }}
            onMouseEnter={(e) => {
              e.currentTarget.style.transform = "scale(1.05)";
              e.currentTarget.style.boxShadow = "0 6px 12px rgba(0,0,0,0.15)";
            }}
            onMouseLeave={(e) => {
              e.currentTarget.style.transform = "scale(1)";
              e.currentTarget.style.boxShadow = "0 4px 8px rgba(0,0,0,0.1)";
            }}
          >
            <div>
              <h3 style={{ margin: 0, fontSize: "1rem", color: "#6b7280" }}>
                {card.title}
              </h3>
              <h2 style={{ margin: "0.5rem 0 0", fontSize: "1.5rem" }}>
                {card.value}
              </h2>
            </div>
            <div
              style={{
                backgroundColor: card.color,
                color: "white",
                borderRadius: "50%",
                padding: "0.5rem",
              }}
            >
              {card.icon}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
