import React, { useState, useEffect } from 'react';
import AddFormModal from './AddFormModal';
import { getPayments, addPayment, updatePayment, deletePayment, getLeases, getUnits, getTenants } from '../api';
import '../app.css';

const Payments = () => {
    const [payments, setPayments] = useState([]);
    const [leases, setLeases] = useState([]);
    const [units, setUnits] = useState([]);
    const [tenants, setTenants] = useState([]);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [currentPayment, setCurrentPayment] = useState(null);
    const [searchTerm, setSearchTerm] = useState('');
    const [loading, setLoading] = useState(true);

    const paymentFields = [
        { name: 'lease_id', label: 'Lease', type: 'select', placeholder: 'Select Lease' },
        { name: 'amount', label: 'Amount', type: 'number', placeholder: 'Enter amount paid' },
        { name: 'payment_date', label: 'Payment Date', type: 'date', placeholder: 'YYYY-MM-DD' },
        { name: 'payment_method', label: 'Payment Method', type: 'text', placeholder: 'Enter payment method' },
        { name: 'notes', label: 'Notes', type: 'text', placeholder: 'Any additional notes' },
    ];

    useEffect(() => {
        const fetchInitialData = async () => {
            try {
                const paymentsData = await getPayments();
                const leasesData = await getLeases();
                const unitsData = await getUnits();
                const tenantsData = await getTenants();
                setPayments(paymentsData);
                setLeases(leasesData);
                setUnits(unitsData);
                setTenants(tenantsData);
                setLoading(false);
            } catch (error) {
                console.error("Failed to fetch initial data:", error);
                setLoading(false);
            }
        };
        fetchInitialData();
    }, []);

    const handleSearchChange = (e) => {
        setSearchTerm(e.target.value);
    };

    const handleAddClick = () => {
        setCurrentPayment(null);
        setIsModalOpen(true);
    };

    const handleEditClick = (payment) => {
        setCurrentPayment(payment);
        setIsModalOpen(true);
    };

    const handleDeleteClick = async (id) => {
        if (window.confirm("Are you sure you want to delete this payment?")) {
            await deletePayment(id);
            setPayments(payments.filter(payment => payment.id !== id));
        }
    };

    const handleAddOrUpdatePayment = async (data) => {
        try {
            if (currentPayment) {
                const updatedPayment = await updatePayment(currentPayment.id, data);
                setPayments(payments.map(payment => payment.id === updatedPayment.id ? updatedPayment : payment));
            } else {
                await addPayment(data);
                const updatedPayments = await getPayments();
                setPayments(updatedPayments);
            }
            setIsModalOpen(false);
        } catch (error) {
            console.error('Failed to save payment:', error);
            if (error.response) {
                console.error('Error response data:', error.response.data);
            }
        }
    };

    const filteredPayments = payments.filter(payment => {
        if (!payment || !payment.lease) return false;
        const searchText = searchTerm.toLowerCase();

        const unit = units.find(u => u.id === payment.lease.unit_id);
        const tenant = tenants.find(t => t.id === payment.lease.tenant_id);

        return (
            (unit?.unit_number && unit.unit_number.toLowerCase().includes(searchText)) ||
            (tenant?.name && tenant.name.toLowerCase().includes(searchText)) ||
            (payment.amount && String(payment.amount).toLowerCase().includes(searchText)) ||
            (payment.payment_method && payment.payment_method.toLowerCase().includes(searchText))
        );
    });

    const getLeaseDisplay = (leaseId) => {
        const lease = leases.find(l => l.id === leaseId);
        if (!lease) return 'N/A';
        const unit = units.find(u => u.id === lease.unit_id);
        const tenant = tenants.find(t => t.id === lease.tenant_id);
        return `${unit?.unit_number || 'N/A'} - ${tenant?.name || 'N/A'}`;
    };

    if (loading) {
        return <div className="main-content">Loading...</div>;
    }

    return (
        <div className="main-content">
            <header className="header">
                <h2>Payments</h2>
                <p>Track all rental payments</p>
                <div className="header-actions">
                    <input
                        type="text"
                        placeholder="Search payments..."
                        value={searchTerm}
                        onChange={handleSearchChange}
                        className="search-input"
                    />
                    <button className="add-button" onClick={handleAddClick}>+ Add Payment</button>
                </div>
            </header>
            <div className="table-container">
                <table className="data-table">
                    <thead>
                        <tr>
                            <th>Lease (Unit - Tenant)</th>
                            <th>Amount</th>
                            <th>Date</th>
                            <th>Method</th>
                            <th>Notes</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {filteredPayments.map(payment => (
                            <tr key={payment.id}>
                                <td>{getLeaseDisplay(payment.lease_id)}</td>
                                <td>KSH {payment.amount}</td>
                                <td>{payment.payment_date}</td>
                                <td>{payment.payment_method}</td>
                                <td>{payment.notes}</td>
                                <td>
                                    <button className="action-button edit-button" onClick={() => handleEditClick(payment)}>Edit</button>
                                    <button className="action-button delete-button" onClick={() => handleDeleteClick(payment.id)}>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            {isModalOpen && (
                <AddFormModal
                    title={currentPayment ? 'Edit Payment' : 'Add New Payment'}
                    fields={paymentFields.map(field => {
                        if (field.name === 'lease_id') {
                            return {
                                ...field,
                                options: leases.map(l => ({
                                    value: l.id,
                                    label: getLeaseDisplay(l.id)
                                }))
                            };
                        }
                        return field;
                    })}
                    initialData={currentPayment}
                    onSubmit={handleAddOrUpdatePayment}
                    onClose={() => setIsModalOpen(false)}
                />
            )}
        </div>
    );
};

export default Payments;
