import React, { useState, useEffect } from "react";
import { getProperties, getTenants } from '../api';

const AddFormModal = ({ title, fields, initialData, onSubmit, onClose }) => {
  const [formData, setFormData] = useState(initialData || {});
  const [properties, setProperties] = useState([]);
  const [tenants, setTenants] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const propertiesData = await getProperties();
        const tenantsData = await getTenants();
        setProperties(propertiesData);
        setTenants(tenantsData);
      } catch (error) {
        console.error("Failed to fetch data for form options:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  // Ensure fields is an array before mapping
  const dynamicFields = Array.isArray(fields) ? fields.map(field => {
    if (field.name === 'property_id' && properties.length > 0) {
      field.options = properties.map(p => ({ value: p.id, label: p.address }));
    }
    if (field.name === 'tenant_id' && tenants.length > 0) {
      field.options = tenants.map(t => ({ value: t.id, label: t.name }));
    }
    return field;
  }) : [];

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <header className="modal-header">
          <h2>{title}</h2>
          <button className="close-button" onClick={onClose}>&times;</button>
        </header>
        {loading ? (
          <div style={{ padding: '1.5rem', textAlign: 'center' }}>Loading...</div>
        ) : (
          <form onSubmit={handleSubmit}>
            {dynamicFields.map(field => (
              <div className="form-group" key={field.name}>
                <label htmlFor={field.name}>{field.label}</label>
                {field.type === 'select' ? (
                  <select
                    name={field.name}
                    value={formData[field.name] || ''}
                    onChange={handleChange}
                    required
                  >
                    <option value="" disabled>{field.placeholder}</option>
                    {field.options.map(option => (
                      <option key={option.value} value={option.value}>
                        {option.label}
                      </option>
                    ))}
                  </select>
                ) : (
                  <input
                    type={field.type}
                    name={field.name}
                    placeholder={field.placeholder}
                    value={formData[field.name] || ''}
                    onChange={handleChange}
                    required
                  />
                )}
              </div>
            ))}
            <footer className="modal-footer">
              <button type="button" className="cancel-button" onClick={onClose}>Cancel</button>
              <button type="submit" className="submit-button">Save</button>
            </footer>
          </form>
        )}
      </div>
    </div>
  );
};

export default AddFormModal;
