import React, { useState } from "react";
import Header from "./Header.jsx";
import Sidebar from "./Sidebar.jsx";

const Dashboard = () => {
  const [stats] = useState({
    total_properties: 3,
    total_tenants: 2,
    total_rent_collected: 72000,
    pending_payments: 36000,
  });

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <Sidebar />

      {/* Main Content */}
      <div className="flex flex-col flex-1">
        <Header />

        <main className="p-6">
          <h1 className="text-2xl font-bold mb-6">Dashboard</h1>

          {/* Stats Cards */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-6">
            <div className="bg-white shadow rounded-lg p-6 cursor-pointer hover:shadow-md">
              <h2 className="text-sm font-medium text-gray-500">Total Properties</h2>
              <p className="text-2xl font-bold">{stats.total_properties}</p>
            </div>
            <div className="bg-white shadow rounded-lg p-6 cursor-pointer hover:shadow-md">
              <h2 className="text-sm font-medium text-gray-500">Total Tenants</h2>
              <p className="text-2xl font-bold">{stats.total_tenants}</p>
            </div>
            <div className="bg-white shadow rounded-lg p-6 cursor-pointer hover:shadow-md">
              <h2 className="text-sm font-medium text-gray-500">Total Rent Collected (KSH)</h2>
              <p className="text-2xl font-bold">KSH {stats.total_rent_collected.toLocaleString()}</p>
            </div>
            <div className="bg-white shadow rounded-lg p-6 cursor-pointer hover:shadow-md">
              <h2 className="text-sm font-medium text-gray-500">Pending Payments (KSH)</h2>
              <p className="text-2xl font-bold">KSH {stats.pending_payments.toLocaleString()}</p>
            </div>
          </div>
        </main>
      </div>
    </div>
  );
};

export default Dashboard;
