import React, { useState, useEffect } from 'react';
import { getLeases, getUnits, getTenants, addLease, updateLease, deleteLease } from '../api.js';
import AddFormModal from './AddFormModal.jsx';
import '../app.css';

const Leases = ({ searchQuery }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [leases, setLeases] = useState([]);
  const [units, setUnits] = useState([]);
  const [tenants, setTenants] = useState([]);
  const [editingLease, setEditingLease] = useState(null);
  const [loading, setLoading] = useState(true);

  const leaseFields = [
    { 
      name: 'unit_id', 
      label: 'Unit', 
      type: 'select', 
      options: units.map(u => ({
        value: u.id, 
        label: `${u.unit_number} (${u.property?.name || 'N/A'})`
      })) 
    },
    { 
      name: 'tenant_id', 
      label: 'Tenant', 
      type: 'select', 
      options: tenants.map(t => ({ value: t.id, label: t.name || 'N/A' })) 
    },
    { name: 'start_date', label: 'Start Date', type: 'date' },
    { name: 'end_date', label: 'End Date', type: 'date' },
    { name: 'monthly_rent', label: 'Monthly Rent', type: 'number', placeholder: 'Enter monthly rent' },
    { 
      name: 'status', 
      label: 'Status', 
      type: 'select', 
      options: [
        { value: 'Active', label: 'Active' },
        { value: 'Expired', label: 'Expired' },
        { value: 'Upcoming', label: 'Upcoming' }
      ] 
    }
  ];

  const refreshData = async () => {
    setLoading(true);
    try {
      const [leasesData, unitsData, tenantsData] = await Promise.all([
        getLeases(),
        getUnits(),
        getTenants()
      ]);
      setLeases(leasesData);
      setUnits(unitsData);
      setTenants(tenantsData);
    } catch (error) {
      console.error("Failed to fetch data:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { refreshData(); }, []);

  const handleOpenModal = (lease = null) => { setEditingLease(lease); setIsModalOpen(true); };
  const handleCloseModal = () => { setIsModalOpen(false); setEditingLease(null); };

  const handleAddOrUpdateLease = async (newLease) => {
    try {
      const leaseData = { ...newLease, rent_amount: newLease.monthly_rent };
      delete leaseData.monthly_rent;
      editingLease ? await updateLease(editingLease.id, leaseData) : await addLease(leaseData);
      refreshData();
    } catch (error) {
      console.error("Failed to save lease:", error.message);
    } finally {
      handleCloseModal();
    }
  };

  const handleDeleteLease = async (id) => {
    try { await deleteLease(id); refreshData(); } 
    catch (error) { console.error("Failed to delete lease:", error.message); }
  };

  const filteredLeases = leases.filter(lease => {
    if (!searchQuery) return true;
    const searchTerm = searchQuery.toLowerCase();
    const unitLabel = lease.unit ? `${lease.unit.unit_number} (${lease.unit.property?.name || 'N/A'})` : '';
    const tenantName = lease.tenant?.name || '';
    const startDate = lease.start_date || '';
    const endDate = lease.end_date || '';
    const rentAmount = lease.rent_amount != null ? String(lease.rent_amount) : '';
    return unitLabel.toLowerCase().includes(searchTerm) ||
           tenantName.toLowerCase().includes(searchTerm) ||
           startDate.includes(searchTerm) ||
           endDate.includes(searchTerm) ||
           rentAmount.includes(searchTerm);
  });

  if (loading) return <div className="page-content"><h1>Loading Leases...</h1></div>;

  return (
    <div className="page-content">
      <div className="page-header">
        <div>
          <h1 style={{ margin: 0, fontSize: '1.5rem', fontWeight: 'bold' }}>Leases</h1>
          <p style={{ margin: '0.5rem 0 0', color: '#6c757d' }}>Manage your property leases</p>
        </div>
        <button onClick={() => handleOpenModal()} className="submit-button">+ Add Lease</button>
      </div>

      <div className="table-container">
        <table>
          <thead>
            <tr>
              <th>Unit</th><th>Tenant</th><th>Start Date</th><th>End Date</th><th>Monthly Rent</th><th>Status</th><th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredLeases.map(lease => (
              <tr key={lease.id}>
                <td>{lease.unit ? `${lease.unit.unit_number} (${lease.unit.property?.name || 'N/A'})` : 'N/A'}</td>
                <td>{lease.tenant?.name || 'N/A'}</td>
                <td>{lease.start_date}</td>
                <td>{lease.end_date || 'N/A'}</td>
                <td>KSH {lease.rent_amount}</td>
                <td>{lease.status}</td>
                <td className="action-buttons">
                  <button onClick={() => handleOpenModal(lease)}>Edit</button>
                  <button onClick={() => handleDeleteLease(lease.id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {isModalOpen && (
        <AddFormModal
          title={editingLease ? "Edit Lease" : "Add New Lease"}
          fields={leaseFields}
          initialData={editingLease}
          onSubmit={handleAddOrUpdateLease}
          onClose={handleCloseModal}
        />
      )}
    </div>
  );
};

export default Leases;
