// src/components/Sidebar.jsx
import React from "react";
import { Link, useLocation } from "react-router-dom";

const navItems = [
  { name: "Dashboard", path: "/", icon: "🏠" },
  { name: "Properties", path: "/properties", icon: "🏡" },
  { name: "Units", path: "/units", icon: "🏢" },
  { name: "Agents", path: "/agents", icon: "👤" },
  { name: "Tenants", path: "/tenants", icon: "👥" },
  { name: "Leases", path: "/leases", icon: "📜" },
  { name: "Payments", path: "/payments", icon: "💵" },
  { name: "Reports", path: "/reports", icon: "📊" },
  { name: "Settings", path: "/settings", icon: "⚙️" },
];

const Sidebar = () => {
  const location = useLocation();

  return (
    <div className="w-72 bg-white min-h-screen shadow-md flex flex-col justify-between">
      <div>
        {/* Logo / Title */}
        <div className="flex items-center gap-2 px-6 py-6 text-blue-600 font-bold text-xl">
          <span className="bg-blue-600 text-white px-3 py-2 rounded-md">RI</span>
          <span className="text-gray-800">Rentals & Income</span>
        </div>

        {/* Nav Links */}
        <ul className="space-y-2 px-4">
          {navItems.map((item) => (
            <li key={item.name}>
              <Link
                to={item.path}
                className={`flex items-center gap-3 px-4 py-3 rounded-lg transition ${
                  location.pathname === item.path
                    ? "bg-blue-100 text-blue-600 font-semibold"
                    : "text-gray-700 hover:bg-gray-100"
                }`}
              >
                <span>{item.icon}</span>
                {item.name}
              </Link>
            </li>
          ))}
        </ul>
      </div>

      {/* Footer */}
      <div className="px-6 py-4 text-sm text-gray-500 border-t">
        <p>Tip: Use the + Add button on each page to create new items.</p>
      </div>
    </div>
  );
};

export default Sidebar;
