import React, { useEffect, useState } from "react";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from "recharts";
import Header from "./Header";
import Sidebar from "./Sidebar";

const Dashboard = () => {
  const [stats, setStats] = useState({
    total_properties: 3,
    total_tenants: 2,
    total_rent_collected: 72000,
    pending_payments: 36000,
  });

  const incomeData = [
    { month: "Oct", income: 0 },
    { month: "Nov", income: 0 },
    { month: "Dec", income: 36000 },
    { month: "Jan", income: 36000 },
    { month: "Feb", income: 0 },
    { month: "Mar", income: 0 },
    { month: "Apr", income: 0 },
    { month: "May", income: 0 },
    { month: "Jun", income: 0 },
    { month: "Jul", income: 0 },
    { month: "Aug", income: 0 },
    { month: "Sep", income: 0 },
  ];

  const cards = [
    { label: "Total Properties", value: stats.total_properties },
    { label: "Total Tenants", value: stats.total_tenants },
    { label: "Total Rent Collected (KSH)", value: `KSH ${stats.total_rent_collected.toLocaleString()}` },
    { label: "Pending Payments (KSH)", value: `KSH ${stats.pending_payments.toLocaleString()}` },
  ];

  return (
    <div className="flex bg-gray-100 min-h-screen">
      {/* Sidebar */}
      <Sidebar />

      {/* Main Content */}
      <div className="flex-1">
        <Header />

        <main className="p-8">
          <h1 className="text-2xl font-bold mb-6">Dashboard</h1>

          {/* Cards */}
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
            {cards.map((card, i) => (
              <div
                key={i}
                className="bg-white p-6 rounded-lg shadow-md cursor-pointer hover:shadow-lg transition"
              >
                <h2 className="text-gray-500 text-sm mb-2">{card.label}</h2>
                <p className="text-xl font-bold text-gray-800">{card.value}</p>
              </div>
            ))}
          </div>

          {/* Chart */}
          <div className="bg-white p-6 rounded-lg shadow-md">
            <h2 className="text-lg font-bold mb-4">Income Trend (Last 12 Months)</h2>
            <ResponsiveContainer width="100%" height={300}>
              <LineChart data={incomeData}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="month" />
                <YAxis />
                <Tooltip />
                <Line type="monotone" dataKey="income" stroke="#2563eb" strokeWidth={3} />
              </LineChart>
            </ResponsiveContainer>
          </div>
        </main>
      </div>
    </div>
  );
};

export default Dashboard;
