import React, { useState, useEffect } from 'react';
import AddFormModal from './AddFormModal';
import { getLeases, addLease, updateLease, deleteLease } from '../api';
import '../app.css';

const Leases = () => {
    const [leases, setLeases] = useState([]);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [currentLease, setCurrentLease] = useState(null);
    const [searchTerm, setSearchTerm] = useState('');

    const leaseFields = [
        { name: 'property_id', label: 'Property', type: 'select', placeholder: 'Select Property', options: [] },
        { name: 'tenant_id', label: 'Tenant', type: 'select', placeholder: 'Select Tenant', options: [] },
        { name: 'start_date', label: 'Start Date', type: 'date', placeholder: 'YYYY-MM-DD' },
        { name: 'end_date', label: 'End Date', type: 'date', placeholder: 'YYYY-MM-DD' },
        { name: 'monthly_rent', label: 'Monthly Rent', type: 'number', placeholder: 'Enter monthly rent' },
        { name: 'status', label: 'Status', type: 'select', placeholder: 'Select Status', options: [
            { value: 'active', label: 'Active' },
            { value: 'expired', label: 'Expired' },
            { value: 'pending', label: 'Pending' }
        ] },
    ];

    useEffect(() => {
        const fetchLeases = async () => {
            const leasesData = await getLeases();
            setLeases(leasesData);
        };
        fetchLeases();
    }, []);

    const handleSearchChange = (e) => {
        setSearchTerm(e.target.value);
    };

    const filteredLeases = leases.filter(lease => {
        const searchText = searchTerm.toLowerCase();
        return (
            (lease.property && lease.property.address.toLowerCase().includes(searchText)) ||
            (lease.tenant && lease.tenant.name.toLowerCase().includes(searchText)) ||
            (lease.status && lease.status.toLowerCase().includes(searchText)) ||
            (lease.monthly_rent && String(lease.monthly_rent).toLowerCase().includes(searchText))
        );
    });

    const handleAddClick = () => {
        setCurrentLease(null);
        setIsModalOpen(true);
    };

    const handleEditClick = (lease) => {
        setCurrentLease(lease);
        setIsModalOpen(true);
    };

    const handleDeleteClick = async (id) => {
        if (window.confirm("Are you sure you want to delete this lease?")) {
            await deleteLease(id);
            setLeases(leases.filter(lease => lease.id !== id));
        }
    };

    const handleAddOrUpdateLease = async (data) => {
        try {
            if (currentLease) {
                const updatedLease = await updateLease(currentLease.id, data);
                setLeases(leases.map(lease => lease.id === updatedLease.id ? updatedLease : lease));
            } else {
                const newLease = await addLease(data);
                setLeases([...leases, newLease]);
            }
            setIsModalOpen(false);
        } catch (error) {
            console.error('Failed to save lease:', error);
            // Consider displaying an error message to the user
        }
    };

    return (
        <div className="main-content">
            <header className="header">
                <h2>Leases</h2>
                <p>Manage your property leases</p>
                <div className="header-actions">
                    <input
                        type="text"
                        placeholder="Search leases..."
                        value={searchTerm}
                        onChange={handleSearchChange}
                        className="search-input"
                    />
                    <button className="add-button" onClick={handleAddClick}>+ Add Lease</button>
                </div>
            </header>
            <div className="table-container">
                <table className="data-table">
                    <thead>
                        <tr>
                            <th>Property</th>
                            <th>Tenant</th>
                            <th>Start Date</th>
                            <th>End Date</th>
                            <th>Monthly Rent</th>
                            <th>Status</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {filteredLeases.map((lease) => (
                            <tr key={lease.id}>
                                <td>{lease.property?.address || 'N/A'}</td>
                                <td>{lease.tenant?.name || 'N/A'}</td>
                                <td>{lease.start_date}</td>
                                <td>{lease.end_date}</td>
                                <td>KSH {lease.monthly_rent}</td>
                                <td>{lease.status}</td>
                                <td>
                                    <button className="action-button edit-button" onClick={() => handleEditClick(lease)}>Edit</button>
                                    <button className="action-button delete-button" onClick={() => handleDeleteClick(lease.id)}>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            {isModalOpen && (
                <AddFormModal
                    title={currentLease ? 'Edit Lease' : 'Add New Lease'}
                    fields={leaseFields}
                    initialData={currentLease}
                    onSubmit={handleAddOrUpdateLease}
                    onClose={() => setIsModalOpen(false)}
                />
            )}
        </div>
    );
};

export default Leases;
