import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const navItems = [
  { name: 'Dashboard', path: '/', icon: 'ðŸ ' },
  { name: 'Properties', path: '/properties', icon: 'ðŸ¡' },
  { name: 'Units', path: '/units', icon: 'ðŸ¢' },
  { name: 'Agents', path: '/agents', icon: 'ðŸ‘¤' },
  { name: 'Tenants', path: '/tenants', icon: 'ðŸ‘¥' },
  { name: 'Leases', path: '/leases', icon: 'ðŸ“œ' },
  { name: 'Payments', path: '/payments', icon: 'ðŸ’µ' },
  { name: 'Reports', path: '/reports', icon: 'ðŸ“Š' },
  { name: 'Settings', path: '/settings', icon: 'âš™ï¸' }
];

const Sidebar = ({ currentTheme = {} }) => {
  const location = useLocation();

  // fallback colors if theme is missing
  const sidebarBg = currentTheme.sidebar || '#ffffff';
  const primaryColor = currentTheme.primary || '#2563eb';
  const textColor = currentTheme.text || '#333333';
  const borderColor = currentTheme.border || '#e5e7eb';
  const linkColor = currentTheme.link || primaryColor;

  return (
    <div style={{
      width: '280px',
      backgroundColor: sidebarBg,
      height: '100vh',
      padding: '2rem 1rem',
      boxShadow: '0 0 10px rgba(0,0,0,0.05)',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'space-between'
    }}>
      <div>
        {/* Logo */}
        <div style={{
          marginBottom: '2rem',
          display: 'flex',
          alignItems: 'center',
          color: primaryColor,
          fontSize: '1.5rem',
          fontWeight: 'bold'
        }}>
          <span style={{
            backgroundColor: primaryColor,
            color: 'white',
            padding: '0.5rem',
            borderRadius: '8px',
            marginRight: '0.5rem'
          }}>
            RI
          </span>
          <span style={{ color: textColor }}>Rentals & Income</span>
        </div>

        {/* Nav links */}
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {navItems.map(item => (
            <li key={item.name} style={{ marginBottom: '0.5rem' }}>
              <Link
                to={item.path}
                style={{
                  textDecoration: 'none',
                  display: 'block',
                  padding: '1rem',
                  borderRadius: '8px',
                  backgroundColor: location.pathname === item.path ? borderColor : 'transparent',
                  color: location.pathname === item.path ? linkColor : textColor,
                  fontWeight: location.pathname === item.path ? 'bold' : 'normal',
                  transition: 'background-color 0.2s'
                }}
              >
                <span style={{ marginRight: '1rem' }}>{item.icon}</span>
                {item.name}
              </Link>
            </li>
          ))}
        </ul>
      </div>

      {/* Footer */}
      <div style={{
        fontSize: '0.8rem',
        color: textColor,
        padding: '1rem',
        borderTop: `1px solid ${borderColor}`,
        marginTop: '1rem'
      }}>
        <p>Tip: Use the + Add button on each data page to create new items.</p>
      </div>
    </div>
  );
};

export default Sidebar;
