import { useEffect, useState } from "react";

function Dashboard() {
  const [summary, setSummary] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch("http://localhost:5000/dashboard_summary")
      .then((res) => res.json())
      .then((data) => {
        setSummary(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Error fetching dashboard summary:", err);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <p className="text-center text-gray-500 mt-6">Loading dashboard...</p>;
  }

  if (!summary) {
    return <p className="text-center text-red-500 mt-6">No data available</p>;
  }

  const items = [
    { label: "Total Properties", value: summary.total_properties },
    { label: "Total Tenants", value: summary.total_tenants },
    { label: "Total Leases", value: summary.total_leases },
    { label: "Available Units", value: summary.available_units },
    { label: "Total Rent Due", value: `$${summary.total_rent_due}` },
  ];

  return (
    <div className="p-8 bg-gray-50 min-h-screen">
      <h2 className="text-2xl font-bold mb-6 text-gray-800">Dashboard Summary</h2>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {items.map((item, idx) => (
          <div
            key={idx}
            className="bg-white shadow-md rounded-xl p-6 hover:shadow-lg transition"
          >
            <h3 className="text-lg font-semibold text-gray-700">{item.label}</h3>
            <p className="text-2xl font-bold text-indigo-600 mt-2">{item.value}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Dashboard;
