import React, { useState, useEffect } from 'react';
import { getUnits, getProperties, addUnit, updateUnit, deleteUnit } from '../api.js';
import AddFormModal from './AddFormModal.jsx';
import '../app.css';

const Units = ({ searchQuery }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [units, setUnits] = useState([]);
  const [properties, setProperties] = useState([]);
  const [editingUnit, setEditingUnit] = useState(null);
  const [loading, setLoading] = useState(true);

  // Define the form fields for adding/editing a Unit
  const unitFields = [
    { name: 'unit_number', label: 'Unit Number', type: 'text', placeholder: 'e.g., 1A, 2B' },
    { name: 'property_id', label: 'Property', type: 'select', options: properties.map(p => ({ value: p.id, label: p.name })) },
    { name: 'rent_amount', label: 'Rent Amount', type: 'number', placeholder: 'e.g., 1500' },
    { name: 'bedrooms', label: 'Bedrooms', type: 'number', placeholder: 'e.g., 2' },
    { name: 'bathrooms', label: 'Bathrooms', type: 'number', placeholder: 'e.g., 1' },
    { name: 'status', label: 'Status', type: 'select', options: [
      { value: 'Vacant', label: 'Vacant' },
      { value: 'Occupied', label: 'Occupied' },
      { value: 'Under Maintenance', label: 'Under Maintenance' }
    ]},
    { name: 'is_occupied', label: 'Is Occupied', type: 'checkbox' }
  ];

  const refreshData = async () => {
    setLoading(true);
    try {
      const unitsData = await getUnits();
      const propertiesData = await getProperties();
      setUnits(unitsData);
      setProperties(propertiesData);
    } catch (error) {
      console.error("Failed to fetch data:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    refreshData();
  }, []);

  const handleOpenModal = (unit = null) => {
    setEditingUnit(unit);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setEditingUnit(null);
  };

  const handleAddOrUpdateUnit = async (newUnit) => {
    try {
      if (editingUnit) {
        await updateUnit(editingUnit.id, newUnit);
      } else {
        await addUnit(newUnit);
      }
      refreshData();
    } catch (error) {
      console.error("Failed to save unit:", error.message);
    } finally {
      handleCloseModal();
    }
  };

  const handleDeleteUnit = async (id) => {
    try {
      await deleteUnit(id);
      refreshData();
    } catch (error) {
      console.error("Failed to delete unit:", error.message);
    }
  };

  const filteredUnits = units.filter(unit => {
    const searchTerm = searchQuery.toLowerCase();
    const propertyName = unit.property ? unit.property.name : '';
    return unit.unit_number.toLowerCase().includes(searchTerm) ||
           propertyName.toLowerCase().includes(searchTerm) ||
           String(unit.rent_amount).includes(searchTerm) ||
           String(unit.bedrooms).includes(searchTerm) ||
           String(unit.bathrooms).includes(searchTerm);
  });

  if (loading) {
    return (
      <div className="page-content">
        <h1>Loading Units...</h1>
      </div>
    );
  }

  return (
    <div className="page-content">
      <div className="page-header">
        <div>
          <h1 style={{ margin: 0, fontSize: '1.5rem', fontWeight: 'bold' }}>Units</h1>
          <p style={{ margin: '0.5rem 0 0', color: '#6c757d' }}>Manage your rental units</p>
        </div>
        <button onClick={() => handleOpenModal()} className="submit-button">
          + Add Unit
        </button>
      </div>

      <div className="table-container">
        <table>
          <thead>
            <tr>
              <th>Unit Number</th>
              <th>Property</th>
              <th>Rent Amount</th>
              <th>Bedrooms</th>
              <th>Bathrooms</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredUnits.map(unit => (
              <tr key={unit.id}>
                <td>{unit.unit_number}</td>
                <td>{unit.property ? unit.property.name : 'N/A'}</td>
                <td>{unit.rent_amount}</td>
                <td>{unit.bedrooms}</td>
                <td>{unit.bathrooms}</td>
                <td>{unit.status}</td>
                <td className="action-buttons">
                  <button onClick={() => handleOpenModal(unit)}>Edit</button>
                  <button onClick={() => handleDeleteUnit(unit.id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      {isModalOpen && (
        <AddFormModal
          title={editingUnit ? "Edit Unit" : "Add New Unit"}
          fields={unitFields}
          initialData={editingUnit}
          onSubmit={handleAddOrUpdateUnit}
          onClose={handleCloseModal}
        />
      )}
    </div>
  );
};

export default Units;
