import { useEffect, useState } from "react";
import {
  Home,
  Users,
  FileText,
  Building,
  DollarSign,
} from "lucide-react"; // icons

function Dashboard() {
  const [summary, setSummary] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch("http://localhost:5000/dashboard_summary")
      .then((res) => res.json())
      .then((data) => {
        setSummary(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Error fetching dashboard summary:", err);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return (
      <p className="text-center text-gray-500 mt-6">Loading dashboard...</p>
    );
  }

  if (!summary) {
    return (
      <p className="text-center text-red-500 mt-6">No data available</p>
    );
  }

  const items = [
    {
      label: "Total Properties",
      value: summary.total_properties,
      icon: Home,
      color: "text-indigo-600",
    },
    {
      label: "Total Tenants",
      value: summary.total_tenants,
      icon: Users,
      color: "text-green-600",
    },
    {
      label: "Total Leases",
      value: summary.total_leases,
      icon: FileText,
      color: "text-orange-600",
    },
    {
      label: "Available Units",
      value: summary.available_units,
      icon: Building,
      color: "text-blue-600",
    },
    {
      label: "Total Rent Due",
      value: `$${summary.total_rent_due}`,
      icon: DollarSign,
      color: "text-emerald-600",
    },
  ];

  return (
    <div className="p-8 bg-gray-50 min-h-screen">
      <h2 className="text-3xl font-bold mb-8 text-gray-800">
        Dashboard Summary
      </h2>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {items.map((item, idx) => {
          const Icon = item.icon;
          return (
            <div
              key={idx}
              className="bg-white shadow-md rounded-xl p-6 flex items-center space-x-4 hover:shadow-lg transition"
            >
              <div
                className={`p-3 rounded-full bg-gray-100 ${item.color}`}
              >
                <Icon size={28} />
              </div>
              <div>
                <h3 className="text-lg font-semibold text-gray-700">
                  {item.label}
                </h3>
                <p className="text-2xl font-bold text-gray-900 mt-1">
                  {item.value}
                </p>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default Dashboard;
