import React, { useState, useEffect } from 'react';
import '../app.css';

const AddFormModal = ({ title, fields = [], initialData = {}, onSubmit, onClose }) => {
  const [formData, setFormData] = useState({});

  // Initialize formData with initialData or default values
  useEffect(() => {
    const initialFormState = {};
    fields.forEach(field => {
      initialFormState[field.name] = initialData[field.name] ?? (field.type === 'select' ? (field.options?.[0]?.value || '') : '');
    });
    setFormData(initialFormState);
  }, [fields, initialData]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (onSubmit) onSubmit(formData);
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>{title}</h2>
        <form onSubmit={handleSubmit}>
          {fields.map((field, idx) => {
            if (field.type === 'select') {
              return (
                <div key={idx} className="form-group">
                  <label>{field.label}</label>
                  <select
                    name={field.name}
                    value={formData[field.name] ?? ''}
                    onChange={handleChange}
                  >
                    {field.options?.map((opt, i) => (
                      <option key={i} value={opt.value}>{opt.label}</option>
                    ))}
                  </select>
                </div>
              );
            } else {
              return (
                <div key={idx} className="form-group">
                  <label>{field.label}</label>
                  <input
                    type={field.type}
                    name={field.name}
                    value={formData[field.name] ?? ''}
                    onChange={handleChange}
                    placeholder={field.placeholder || ''}
                  />
                </div>
              );
            }
          })}

          <div className="modal-actions">
            <button type="submit" className="submit-button">Save</button>
            <button type="button" className="cancel-button" onClick={onClose}>Cancel</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddFormModal;
