import React, { useEffect, useState } from "react";
import { Home, Users, Building2, Wallet } from "lucide-react";
import Topbar from "./Topbar";
import Sidebar from "./Sidebar";

const Dashboard = ({ currentTheme, setTheme }) => {
  const [stats, setStats] = useState({
    total_properties: 0,
    occupied_units: 0,
    vacant_units: 0,
    total_monthly_rent: 0,
  });

  // ✅ Fetch data when the component loads
  useEffect(() => {
    const fetchStats = async () => {
      try {
        // Example: adjust URLs to match your backend routes
        const propertiesRes = await fetch("http://localhost:5000/properties");
        const unitsRes = await fetch("http://localhost:5000/units");
        const paymentsRes = await fetch("http://localhost:5000/payments");

        const properties = await propertiesRes.json();
        const units = await unitsRes.json();
        const payments = await paymentsRes.json();

        // Count values dynamically
        const totalProperties = properties.length;
        const occupiedUnits = units.filter((u) => u.status === "occupied").length;
        const vacantUnits = units.filter((u) => u.status === "vacant").length;
        const totalRent = payments.reduce((sum, p) => sum + (p.amount || 0), 0);

        setStats({
          total_properties: totalProperties,
          occupied_units: occupiedUnits,
          vacant_units: vacantUnits,
          total_monthly_rent: totalRent,
        });
      } catch (error) {
        console.error("Failed to fetch stats:", error);
      }
    };

    fetchStats();
  }, []);

  const cards = [
    {
      title: "Total Properties",
      value: stats.total_properties,
      icon: <Building2 size={28} />,
      link: "/properties",
    },
    {
      title: "Occupied Units",
      value: stats.occupied_units,
      icon: <Home size={28} />,
      link: "/units",
    },
    {
      title: "Vacant Units",
      value: stats.vacant_units,
      icon: <Users size={28} />,
      link: "/units",
    },
    {
      title: "Monthly Rent",
      value: `Ksh ${stats.total_monthly_rent}`,
      icon: <Wallet size={28} />,
      link: "/payments",
    },
  ];

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <Sidebar />

      {/* Main content area */}
      <div className="flex-1 flex flex-col">
        {/* ✅ Topbar */}
        <Topbar currentTheme={currentTheme} setTheme={setTheme} />

        {/* Dashboard cards */}
        <main className="p-6">
          <h1 className="text-2xl font-bold mb-6">Dashboard</h1>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
            {cards.map((card, index) => (
              <a
                key={index}
                href={card.link}
                className="bg-white shadow rounded-xl p-6 flex flex-col items-start gap-4 hover:shadow-lg transition"
              >
                <div className="text-blue-600">{card.icon}</div>
                <div>
                  <h2 className="text-lg font-semibold">{card.title}</h2>
                  <p className="text-2xl font-bold">{card.value}</p>
                </div>
              </a>
            ))}
          </div>
        </main>
      </div>
    </div>
  );
};

export default Dashboard;
