import React, { useState } from "react";
import AddFormModal from "./AddFormModal";

const Leases = ({ leases, onAddLease, onDeleteLease }) => {
  const [searchQuery, setSearchQuery] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Safe filtering for search
  const filteredLeases = leases
    .filter(lease => lease != null)
    .filter(lease => {
      const term = searchQuery.toLowerCase();
      const unitNumber = lease.unit?.unit_number?.toLowerCase() || "";
      const tenantName = lease.tenant?.name?.toLowerCase() || "";
      return unitNumber.includes(term) || tenantName.includes(term);
    });

  const handleOpenModal = () => setIsModalOpen(true);
  const handleCloseModal = () => setIsModalOpen(false);

  const handleAddLease = (newLease) => {
    onAddLease?.(newLease);
    handleCloseModal();
  };

  return (
    <div>
      <h2>Leases</h2>
      <input
        type="text"
        placeholder="Search by unit or tenant..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <button onClick={handleOpenModal}>Add Lease</button>

      {filteredLeases.length === 0 ? (
        <p>No leases found.</p>
      ) : (
        <ul>
          {filteredLeases.map(lease => (
            <li key={lease?.lease_id ?? Math.random()}>
              <span>Unit: {lease.unit?.unit_number ?? "N/A"}</span> |{" "}
              <span>Tenant: {lease.tenant?.name ?? "N/A"}</span> |{" "}
              <span>Start: {lease.start_date ?? "N/A"}</span> |{" "}
              <span>End: {lease.end_date ?? "N/A"}</span>
              <button onClick={() => onDeleteLease?.(lease.lease_id)}>Delete</button>
            </li>
          ))}
        </ul>
      )}

      {isModalOpen && (
        <AddFormModal onSubmit={handleAddLease} onClose={handleCloseModal} />
      )}
    </div>
  );
};

export default Leases;
